  const social_numberHandler = (event) => {
    //
  };
  const categoryHandler = (event) => {
    //
  };
  const submitHandler = (event) => {
    //
  };
  return (
    <form onSubmit={submitHandler} className="form">
      <div className="form-item">
        <label for="name">Name</label>
        <input
          onChange={nameHandler}
          value={name}
          type="text"
          placeholder="Enter your full name"
        />
      </div>
      <div className="form-item">
        <label for="name">Address</label>
        <input
          onChange={addressHandler}
          value={address}
          type="text"
          placeholder="Enter your address"
        />
      </div>
      <div className="form-item">
        <label for="name">Social Number</label>
        <input
          onChange={social_numberHandler}
          value={social_number}
          type="number"
          placeholder="Enter your citizenship/nid id"
        />
      </div>
      <div className="form-item">
        <label for="name">Select Category</label>
        <select
          onClick={categoryHandler}
          value={category}
          id="category"
          name="category"
        >
          <option value="option1">President</option>
          <option value="option2">Vice President</option>
          <option value="option3">Secetary</option>
        </select>
      </div>
      <div className="form-item">
        <button className="btn" type="submit">
          Add Candidate
        </button>
      </div>
    </form>
  );
};

export default CandidateForm;
