import { useState, useEffect, useRef } from "react";
import axios from "axios";
import CategorySelectionForm from "./CategorySelectionForm";
const Form = (props) => {

  //disabling multiple clicks 
  let btnRef = useRef();
  const onBtnClick = (e) => {
    if (btnRef.current) {
      btnRef.current.setAttribute("disabled", "disabled");
    }
  };


  const [candidates, setCandidates] = useState([]);

  //fetching candidate data
  const [candidateData, setCandidateData] = useState([]);
  useEffect(() => {
    axios({
      method: "get",
      url: "http://localhost:3001/get-candidate",
      responseType: "json",
    }).then((response) => {
      setCandidateData(response.data.data);
    });
  }, []);

  //filtering data according to category
  const candidateArray = candidateData.filter((item) => {
    if (props.category === item.category) return item;
    else return null;
  });

  //   console.log("Candidate Array");
  //   console.log(candidateArray[0]);

  //   console.log(candidateArray);
  const categoryHandler = (event) => {
    // setCategory(event.target.value);
    props.onCandidatesHandler(event.target.value);
    // setCandidates([...candidates, event.target.value]);
  };

  return (
    <div className="form-item">
      <label for="president">{props.category}</label>
      <select onClick={categoryHandler} name="" id="">
        {candidateArray.map((item) => (
          // <CategorySelectionForm name={item.name} />
          <option  ref={btnRef} value={item.name}>{item.name}</option>
        ))}
      </select>
    </div>
  );
};

export default Form;
