import { useState, useEffect } from "react";
import axios from "axios";
const Form = (props) => {
    let candidateArray = [];
  //fetching candidate data
  const [candidateData, setCandidateData] = useState([]);
  useEffect(() => {
    axios({
      method: "get",
      url: "http://localhost:3001/get-candidate",
      responseType: "json",
    }).then((response) => {
      setCandidateData(response.data.data);
    });
  }, []);
  candidateArray.push(candidateData.filter((item)=>{
    if(item.category == props.category ) {
        return{
            name : item.name,
            address : item.address,
            social_number : item.social_number
        }
    }
  })) 
  return (
    <div className="form-item">
      <label for="president">{props.category}</label>
      <select name="" id="">
        <option value="">Aastha Shrestha</option>
        <option value="">ABCD ABCD</option>
        <option value="">XYZ XYZ</option>
      </select>
    </div>
  );
};

export default Form;
