import { useState, useEffect } from "react";
import axios from "axios";
import Form from "../components/vote/Form";
const VoteForm = () => {
  const candidatesHandler =(candidates) =>{
    console.log(candidates)
  }
  // fetching categories
  const [categoryData, setCategoryData] = useState([]);
  useEffect(() => {
    axios({
      method: "get",
      url: "http://localhost:3001/get-category",
      responseType: "json",
    }).then((response) => {
      setCategoryData(response.data.data);
    });
  }, []);


  return (
    <form action="">
      {categoryData.map((item) => (
        <Form category={item.category_item} onCandidatesHandler={candidatesHandler} />
      ))}
      <div className="form-item">
        <label for="president">Enter Social Number</label>
        <input type="number" required />
      </div>
      <div className="form-item">
        <button type="submit">Vote</button>
      </div>
    </form>
  );
};

export default VoteForm;
