import { useState, useEffect } from "react";
import axios from "axios";
import CategorySelectionForm from "../../form/CategorySelectionForm";
const Form = (props) => {
  let candidateArray = [];
  //fetching candidate data
  const [candidateData, setCandidateData] = useState([]);
  useEffect(() => {
    axios({
      method: "get",
      url: "http://localhost:3001/get-candidate",
      responseType: "json",
    }).then((response) => {
      setCandidateData(response.data.data);
    });
  }, []);
  candidateArray.push(
    candidateData.filter((item) => {
      if (item.category == props.category) {
        return {
          name: item.name,
          category: item.category,
        };
      }
    })
  );
  return (
    <div className="form-item">
      <label for="president">{props.category}</label>
      <select name="" id="">
        {candidateArray.map((item) => (
          <CategorySelectionForm name={item.name} category={item.category} />
        ))}
      </select>
    </div>
  );
};

export default Form;
