import { useState, useEffect } from "react";
import axios from "axios";
import Category from "../../components/Category";
const ManageCategory = () => {
  let categoryDataArray = [];
  const [categoryData, setCategoryData] = useState("");
  useEffect(() => {
    axios({
      method: "get",
      url: "http://localhost:3001/get-category",
      responseType: "json",
    }).then((response) => {
      setCategoryData(response.data.data); //sets an array of objects
    });
  }, []);

  // console.log(categoryData);
  // console.log(typeof(categoryData));
  const objToArray = (parameter) => {
    for (let index = 0; index < parameter.length; index++) {
      const element = parameter[index];
      console.log(element)
    }
  };
  objToArray(categoryData);

  return;
  // return categoryData.map((item) => <Category category={categoryData.category_item} />);
};

export default ManageCategory;
