import { useState, useEffect } from "react";
import axios from "axios";
import Category from "../../components/Category";
const ManageCategory = () => {
  const [categoryData, setCategoryData] = useState("");
  useEffect(() => {
    axios({
      method: "get",
      url: "http://localhost:3001/get-category",
      responseType: "json",
    }).then((response) => {
      setCategoryData(response.data.data); //sets an array of objects
    });
  }, []);
  console.log("executing for loop");
  for (let i = 0; i < categoryData.length; i++) {
    const element = categoryData[i];
    console.log(element);
    console.log(element.category_item);
  }

  // return (
  //   categoryData.data.map(item =>{
  //     <Category category = {item.category_item}/>
  //   })
  // )


};

export default ManageCategory;
