import { useState, useEffect } from "react";
import axios from "axios";
import Form from "../components/vote/Form";
const VoteForm = () => {
  const [voteData, setVoteData] = useState([]);

  const voteDataHandler = (data) => {

    //filtering repetation of voteData
    console.log(data.category);

    const filteredVoteData = voteData.filter((item) => {
      if (item.category !== data.category) {
        return item;
      }
    });
    setVoteData([...filteredVoteData, data]);

  };

  // fetching categories
  const [categoryData, setCategoryData] = useState([]);
  useEffect(() => {
    axios({
      method: "get",
      url: "http://localhost:3001/get-category",
      responseType: "json",
    }).then((response) => {
      setCategoryData(response.data.data);
    });
  }, []);

  const submitHandler = (event) => {
    event.preventDefault();
    console.log(voteData);
  };

  return (
    <form onSubmit={submitHandler} action="">
      {categoryData.map((item) => (
        <Form
          category={item.category_item}
          onVoteDataHandler={voteDataHandler}
        />
      ))}
      <div className="form-item">
        <label for="president">Enter Social Number</label>
        <input type="number" required />
      </div>
      <div className="form-item">
        <button type="submit">Vote</button>
      </div>
    </form>
  );
};

export default VoteForm;
